import React, { useState } from 'react';
import './App.css';
import ContactsView from './Components/ContactsView.js';
import AddContactModal from './Components/AddContactModal.js';
import SearchContact from './Components/SearchContact.js';

export default function App() {
  const [data, setData] = useState({ name: '', age: '', number: '' });
  const [contacts, setContacts] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();
    setContacts([...contacts, data]);
    setData({ name: '', age: '', number: '' }); // Clear form fields after submission
  }

  function handleDelete(index) {
    const updatedContacts = [...contacts];
    updatedContacts.splice(index, 1);
    setContacts(updatedContacts);
  }

  return (
    <>
      <div id='main'>
        <nav id='navbar'>
          {/* Navigation components can go here */}
        </nav>
        <form onSubmit={handleSubmit}>
          <p>Your name</p>
          <input type='text' value={data.name} onChange={(e) => setData({ ...data, name: e.target.value })} required />
          <p>Your age</p>
          <input type='number' value={data.age} onChange={(e) => setData({ ...data, age: e.target.value })} required />
          <p>Your phone number</p>
          <input type='number' value={data.number} onChange={(e) => setData({ ...data, number: e.target.value })} required />
          <br />
          <br />
          <button type="submit">Submit</button>
        </form>

        <div>
          {contacts.length > 0 ? (
            contacts.map((contact, index) => (
              <div key={index}>
                <AddContactModal contact={contact} />
                <button onClick={() => handleDelete(index)}>Delete</button>
              </div>
            ))
          ) : (
            <AddContactModal contact={''}/>
          )}
        </div>
      </div>
      <SearchContact contacts={contacts} />
      <ContactsView />
      {/* Additional components can go here */}
    </>
  );
}
