import React, { useState } from 'react';
import './App.css';
import ContactsView from './Components/ContactsView';
import SearchContact from './Components/SearchContact';

export default function App() {
  const [data, setData] = useState({ name: '', age: '', number: '' });
  const [contacts, setContacts] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  function handleSubmit(e) {
    e.preventDefault();
    setContacts([...contacts, data]);
  }

  function handleDelete(index) {
    const updatedContacts = [...contacts];
    updatedContacts.splice(index, 1);
    setContacts(updatedContacts);
  }

  const filteredContacts = contacts.filter(contact =>
    contact.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
      <div id='main'>
        <form onSubmit={handleSubmit}>
          <p>Your name</p>
          <input type='text' value={data.name} onChange={(e) => setData({ ...data, name: e.target.value })} required />
          <p>Your age</p>
          <input type='number' value={data.age} onChange={(e) => setData({ ...data, age: e.target.value })} required />
          <p>Your phone number</p>
          <input type='number' value={data.number} onChange={(e) => setData({ ...data, number: e.target.value })} required />
          <br />
          <br />
          <button type="submit">Submit</button>
        </form>

        <div>
          {filteredContacts.map((contact, index) => (
            <div key={index}>
              <ContactsView contact={contact} />
              <button onClick={() => handleDelete(index)}>Delete</button>
            </div>
          ))}
        </div>
      </div>
      <div>
        <SearchContact
          contacts={contacts}
          searchQuery={searchQuery}
          setSearchQuery={setSearchQuery}
        />
      </div>
    </>
  );
}
