import React, { useState } from 'react';
import ContactsView from './Components/ContactsView';
import AddContactModal from './Components/AddContactModal';
import EditContactModal from './Components/EditContact';
import SearchContact from './Components/SearchContact';

export default function App() {
  const [data, setData] = useState();
  const [add, setAdd] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();
    setAdd([...add, data]);
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type='text' value={data.name} onChange={(e) => setData( e.target.value )}></input>
        <input type='number' value={data.age} onChange={(e) => setData({ ...data, age: e.target.value })}></input>
        <input type='number' value={data.number} onChange={(e) => setData({ ...data, number: e.target.value })}></input>
        <button type="submit">Submit</button>
      </form>

      <div>
        
        {add.map((contact, index) => (
          <ContactsView key={index} contact={contact} />
        ))}
      </div>

      <AddContactModal />
      <EditContactModal />
      <SearchContact />
    </div>
  );
}
