import React, { useState } from 'react';

export default function ContactsView({ contacts }) {
  const [showData, setShowData] = useState([]);

  function showUpdate() {
    setShowData(contacts); // Set showData to contacts array
  }

  function hideUpdate() {
    setShowData([]); // Set showData to an empty array to hide contacts
  }

  return (
    <>
      <div>
        {showData.map((contact, index) => (
          <div key={index}>
            <p>{contact.name}</p>
            <p>{contact.age}</p>
            <p>{contact.number}</p>
          </div>
        ))}
      </div>
      <button onClick={() => showUpdate()}>Show Contacts</button>
      <button onClick={() => hideUpdate()}>Hide Contacts</button>
    </>
  );
}
