// App.js

import React, { useState, useEffect } from 'react';
import ContactsView from './Components/ContactsView'
import AddContactModal from './components/AddContactModal';
import EditContactModal from './components/EditContactModal';
import ViewContactDetailsModal from './components/ViewContactDetailsModal';
import SearchContact from './components/SearchContact';
import sampleData from './sample.json';

function App() {
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch data from sample.json when the component mounts
    setContacts(sampleData);
  }, []);

  // Function to handle adding a new contact
  const handleAddContact = (newContact) => {
    setContacts([...contacts, newContact]);
    setShowAddModal(false);
  };

  // Function to handle editing an existing contact
  const handleEditContact = (editedContact) => {
    const updatedContacts = contacts.map(contact =>
      contact.id === editedContact.id ? editedContact : contact
    );
    setContacts(updatedContacts);
    setShowEditModal(false);
  };

  // Function to handle deleting a contact
  const handleDeleteContact = (id) => {
    const updatedContacts = contacts.filter(contact => contact.id !== id);
    setContacts(updatedContacts);
  };

  // Function to handle viewing contact details
  const handleViewContact = (contact) => {
    setSelectedContact(contact);
    setShowViewModal(true);
  };

  // Function to handle searching contacts
  const handleSearch = (term) => {
    setSearchTerm(term);
  };

  return (
    <div>
      <ContactsView
        contacts={contacts}
        onDelete={handleDeleteContact}
        onView={handleViewContact}
        searchTerm={searchTerm}
      />
      <AddContactModal
        show={showAddModal}
        onClose={() => setShowAddModal(false)}
        onAdd={handleAddContact}
      />
      <EditContactModal
        show={showEditModal}
        onClose={() => setShowEditModal(false)}
        contact={selectedContact}
        onEdit={handleEditContact}
      />
      <ViewContactDetailsModal
        show={showViewModal}
        onClose={() => setShowViewModal(false)}
        contact={selectedContact}
      />
      <SearchContact onSearch={handleSearch} />
    </div>
  );
}

export default App;
