import React, { useState } from 'react';
import './SearchContact.css';

export default function SearchContact({ contacts }) {
  let [search, setSearch] = useState('');
  let [view, setView] = useState('');

  function searchUpdate() {
    if (
      contacts.name.toLowerCase().includes(search.toLowerCase()) ||
      contact.age.toString().includes(search) ||
      contact.number.toString().includes(search)
    ) {
      setView(contact);
    } else {
      setView('');
    }
  }

  return (
    <div>
      <nav>
        <input type='search' placeholder='Search' onChange={(e) => setSearch(e.target.value)} />
        <button  onClick={()=>searchUpdate()}>Search</button>
      </nav>
      <p>{view.name}</p>
      <p>{view.age}</p>
      <p>{view.number}</p>
    </div>
  );
}
