import React, { useState } from 'react';

export default function ContactsView({ contacts }) {
  const [showNumber, setShowNumber] = useState(true);

  function showUpdate() {
    setShowNumber(!showNumber); // Toggle showNumber state
  }

  // Render contacts with optional number based on showNumber state
  return (
    <>
      <div>
        {contacts.map((contact, index) => (
          <div key={index}>
            {/* <p>{contact.name}</p> */}
            {/* <p>{contact.age}</p> */}
            {showNumber && <p>{contact.name}, {contact.n},{contact.age} </p>} 
          </div>
        ))}
      </div>
      <button onClick={showUpdate}>Toggle Number</button>
    </>
  );
}
