import React, { useState } from 'react';
import './SearchContact.css';

export default function SearchContact({ contacts }) {
  const [search, setSearch] = useState('');
  const [view, setView] = useState([]);

  const debounce = (func, delay) => {
    let timeoutId;
    return function (...args) {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(this, args);
      }, delay);
    };
  };

  const searchUpdateDebounced = debounce(searchUpdate, 300);

  function searchUpdate() {
    const filteredContacts = contacts.filter(contact =>
      contact.name.includes(search) ||
      contact.age.toString().includes(search) ||
      contact.number.includes(search)
    );
    setView(filteredContacts);
  }

  function handleSearchChange(e) {
    setSearch(e.target.value);
    searchUpdateDebounced();
  }

  return (
    <div>
      <nav>
        <input type='search' placeholder='Search' onChange={handleSearchChange} />
      </nav>
      <div>
        {view.map((contact, index) => (
          <div key={index}>
            <p>{contact.name}</p>
            <p>{contact.age}</p>
            <p>{contact.number}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
