import React, { useState } from 'react';

export default function ContactsView({ contacts }) {
  const [showData, setShowData] = useState([]);
  // const [ndata, setNdata] = useState(false);

  function showUpdate() {
    if (tr) {
      setShowData(contacts); // Just set contacts directly to showData
    } else {
      setShowData([]); // Reset showData to empty array
    }
  }

  return (
    <>
      <div>
        {showData.map((contact, index) => (
          <div key={index}>
            <p>{contact.name}</p>
            <p>{contact.age}</p>
            <p>{contact.number}</p>
          </div>
        ))}
      </div>
      {/* <button onClick={() => setNdata(true)}>Show Contacts</button>  */}
      <button onClick={() => showUpdate()}>Update Display</button> 
    </>
  );
}
